extension.install:
if (!isset($forum_config['o_pun_funny_avatars_file_dir']) && !file_exists(FORUM_ROOT.'img/avatars/pun_funny_avatars'))
	mkdir(FORUM_ROOT.'img/avatars/pun_funny_avatars');
if (!$forum_db->table_exists('pun_funny_avatars_files'))
{
	$schema = array(
		'FIELDS'			=> array(
			'user_id'			=> array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'width'=> array(
				'datatype'		=> 'INT(5) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			),
			'height'=> array(
				'datatype'		=> 'INT(5) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			)
		),
		'PRIMARY KEY'	=> array('user_id')
	);

	$forum_db->create_table('pun_funny_avatars_files', $schema);
}

if (!$forum_db->table_exists('pun_funny_avatars_result_info'))
{
	$schema = array(
		'FIELDS'            => array(
			'user_id'           => array(
				'datatype'  	=> 'SERIAL',
				'allow_null'    => false
			),
			'result_url'=> array(
				'datatype'      => 'VARCHAR(400)',
				'allow_null'    => false,
			),
		),
		'PRIMARY KEY'   => array('user_id')
	);

	$forum_db->create_table('pun_funny_avatars_result_info', $schema);
}

// Add extension options to the config table
$config = array(
	'o_pun_funny_avatars_file_dir'	=> 'img/avatars/pun_funny_avatars',
	'o_pun_funny_avatars_max_height'	=> '1024',
	'o_pun_funny_avatars_max_width'	=> '1024',
	'o_pun_funny_avatars_max_size'	=> '1048576',
);
foreach ($config as $conf_name => $conf_value)
{
	if(!array_key_exists($conf_name, $forum_config))
	{
		$query = array(
			'INSERT'	=> 'conf_name, conf_value',
			'INTO'		=> 'config',
			'VALUES'	=> '\''.$conf_name.'\', \''.$conf_value.'\''
		);

		$forum_db->query_build($query) or error(__FILE__, __LINE__);
	}
}

extension.uninstall:
$avatars_files = scandir(FORUM_ROOT.$forum_config['o_pun_funny_avatars_file_dir'].DIRECTORY_SEPARATOR);
foreach ($avatars_files as $file)
    if ($file != '.' && $file != '..')
        unlink(FORUM_ROOT.$forum_config['o_pun_funny_avatars_file_dir'].DIRECTORY_SEPARATOR.$file);

rmdir(FORUM_ROOT.$forum_config['o_pun_funny_avatars_file_dir']);

$forum_db->drop_table('pun_funny_avatars_result_info');
$forum_db->drop_table('pun_funny_avatars_files');

$query = array(
	'DELETE'	=> 'config',
	'WHERE'		=> 'conf_name in (\'o_pun_funny_avatars_file_dir\', \'o_pun_funny_avatars_max_height\', \'o_pun_funny_avatars_max_width\', \'o_pun_funny_avatars_max_size\')',
);
$forum_db->query_build($query) or error(__FILE__, __LINE__);

if (file_exists(FORUM_CACHE_DIR.'cache_pun_funny_avatars.php'))
	unlink(FORUM_CACHE_DIR.'cache_pun_funny_avatars.php');

unlink(FORUM_ROOT.$forum_config['o_pun_funny_avatars_file_dir']);

hooks.hook:
if (preg_match('~profile[A-Za-z_\-]*-avatar~', FORUM_PAGE) && $forum_config['o_avatars'])
{
	if (!isset($avatars_links))
		$avatars_links = array();
	$avatars_links['profile-avatar'] = '<li class="'.(FORUM_PAGE == 'profile-avatar' ? 'active ' : '').'first-item"><a href="'.forum_link($forum_url['profile_avatar'], $id).'">'.$lang_pun_funny_avatars['Avatar'].'</a></li>';
	$avatars_links['profile-funny-avatar'] = '<li class="'.(FORUM_PAGE == 'profile-funny-avatar' ? 'active ' : '').'first-item"><a href="'.forum_link($forum_url['funny_avatar'], $id).'">'.$lang_pun_funny_avatars['Funny avatar'].'</a></li>';
}

if (!empty($avatars_links))
{
	$main_elements['<!-- forum_main_menu -->'] .= '<div class="admin-submenu gen-content"><ul>';
	$main_elements['<!-- forum_main_menu -->'] .= implode($avatars_links);
	$main_elements['<!-- forum_main_menu -->'] .= '</ul></div>';
	unset($avatars_links);
}

define('PUN_FUNNY_AVATARS_FREE_KEY', 'TLTYHSFVJBN48QORTVNAKROQMGPW');
define('PUN_FUNNY_AVATARS_IMAGE_LIMIT', 'watermark');
include $ext_info['path'].'/functions.php';
if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php'))
	include $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
else
	include $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';

// Setup the URL rewriting scheme
if (file_exists($ext_info['path'].'/url/'.$forum_config['o_sef'].'.php'))
	require $ext_info['path'].'/url/'.$forum_config['o_sef'].'.php';
else
	require $ext_info['path'].'/url/Default.php';

$forum_url['pho.to_FET_queue'] = 'http://ope-api.pho.to/queued.php?key=$1&image_url=$2&image_limit=$3&methods_list=collage:template_name=$4:crop_portrait=$5&result_size=$6';
$forum_url['pho.to_get-result'] = 'http://ope-api.pho.to/get-result.php?request_id=$1&remove_limit=visit_page';
$forum_url['pho.to_FET_templates'] = 'http://ope-api.pho.to/service.php?method=get_templates&type=FET';

if ($section == 'funny_avatar' && $forum_config['o_avatars'])
{
	//View blocks
	$view_blocks = array();
	$forum_page = array();
	//Add csrf where needed
	if (isset($_POST['photo_upload']))
	{
		$file_type = pun_funny_avatars_get_file_type($id);
		if (!$file_type)
			pun_funny_avatars_upload_photo($id);
		else
			$errors[] = $lang_pun_funny_avatars['File exist'];

		if (!empty($errors))
			$view_blocks[] = 'upload_form';
		else
		{
			$view_blocks[] = 'image_link';
			$view_blocks[] = 'effects';
		}			
	}
	//Sending request to pho.to service
	else if (isset($_POST['fet_template_submit']))
	{
		$pho_to_template = isset($_POST['fet_template']) ? forum_trim($_POST['fet_template']) : FALSE;
		$auto_crop = isset($_POST['auto_crop']) && intval($_POST['auto_crop']) == 1 ? 'true' : 'false';

		if (!is_scalar($pho_to_template))
			message($lang_common['Bad request']);
		if (!$pho_to_template)
		{
			$errors[] = $lang_pun_funny_avatars['No templates'];
			$view_blocks[] = 'image_link';
			$view_blocks[] = 'effects';
		}

		if (empty($errors))
		{
			$file_type = pun_funny_avatars_get_file_type($id);
			if (!$file_type)
				message($lang_common['Bad request']);

			pun_funny_avatars_template($pho_to_template, $id, $file_type, $auto_crop);
		}
		if (!empty($errors))
		{
			$view_blocks[] = 'image_link';
			$view_blocks[] = 'effects';
		}
	}
	//Avatar rewriting
	else if (isset($_GET['rewrite_avatar']))
	{
		if (!isset($_GET['csrf_token']) || $_GET['csrf_token'] !== generate_form_token('rewrite_avatar'.$forum_user['id']))		
			csrf_confirm_form();

        $file_type = pun_funny_avatars_get_file_type($id);
        $tmp_result_image_link = pun_funny_avatars_get_result_info($id);
        if ($tmp_result_image_link !== FALSE)
        {
            $photo_image = get_remote_file($tmp_result_image_link, 10);
			if (!empty($photo_image['content']))
			{
				if (file_exists(FORUM_ROOT.$forum_config['o_avatars_dir'].'/'.$id.'.'.$file_type))
					unlink(FORUM_ROOT.$forum_config['o_avatars_dir'].'/'.$id.'.'.$file_type);
				$avatar_handler = fopen(FORUM_ROOT.$forum_config['o_avatars_dir'].'/'.$id.'.'.substr($tmp_result_image_link, strrpos($tmp_result_image_link, '.') + 1), 'w');
				fwrite($avatar_handler, $photo_image['content']);
				fclose($avatar_handler);
			}
			else
				$errors[] = $lang_pun_funny_avatars['Pho.to error result image'];

			pun_funny_avatars_remove_result_info($id);
			redirect(forum_link($forum_url['profile_avatar'], $id), $lang_profile['Profile redirect']);
		}
		else
			message($lang_common['Bad request']);
	}
	else if (isset($_GET['result_url']))
	{
		if (!is_scalar($_GET['result_url']) || !preg_match('~\.pho\.to~', $_GET['result_url']))
			message($lang_common['Bad request']);

		$forum_page['result_image_link'] = forum_trim($_GET['result_url']);
		pun_funny_avatars_add_result_info($id, $forum_page['result_image_link']);

		$view_blocks[] = 'image_link';
		$view_blocks[] = 'result_link';
		$view_blocks[] = 'effects';
	}
	//Remove file
	else if (isset($_GET['remove_file']))
	{
		if (!isset($_GET['csrf_token']) || $_GET['csrf_token'] !== generate_form_token('remove_file'.$forum_user['id']))
			csrf_confirm_form();
		pun_funny_avatars_remove_photo($id);
		$view_blocks[] = 'upload_form';
	}
	else if (isset($_GET['request_id']))
	{
		if (!is_scalar($_GET['request_id']))
			message($lang_common['Bad request']);
		if (!isset($_GET['csrf_token']) || $_GET['csrf_token'] !== generate_form_token('request_id'.$forum_user['id']))
			csrf_confirm_form();
		pun_funny_avatars_visit_pho_to_page($id, forum_trim($_GET['request_id']));
		if (!empty($errors))
		{
			$view_blocks[] = 'image_url';
			$view_blocks[] = 'effects';
		}
	}
	//Default display
	if (empty($view_blocks))
	{
		if (!pun_funny_avatars_get_file_type($id))
			$view_blocks[] = 'upload_form';
		else
		{
			$view_blocks[] = 'image_link';
			$view_blocks[] = 'effects';
		}
	}
	if (!in_array('result_link', $view_blocks))
	{
		$forum_page['result_image_link'] = pun_funny_avatars_get_result_info($id);
		if ($forum_page['result_image_link'] !== FALSE)
			$view_blocks[] = 'result_link';
	}
	if (file_exists(FORUM_CACHE_DIR.'cache_pun_funny_avatars.php'))
		include FORUM_CACHE_DIR.'cache_pun_funny_avatars.php';
	if (!defined('FUNNY_TEMPLATES_LOADED') || $funny_templates['cached'] < (time() - 43200))
	{
		pun_funny_avatars_generate_templates_cache();
		require FORUM_CACHE_DIR.'cache_pun_funny_avatars.php';
	}
}

if ($section == 'funny_avatar' && $forum_config['o_avatars'])
{
	//View
	if (in_array('effects', $view_blocks) || in_array('upload_form', $view_blocks))
		$forum_page['form_action'] = forum_link($forum_url['funny_avatar'], $id);
	if (in_array('image_link', $view_blocks))
	{
		$forum_page['uploaded_image_link'] = forum_link($forum_config['o_pun_funny_avatars_file_dir']).'/'.$id.'.'.pun_funny_avatars_get_file_type($id);
		$forum_page['uploaded_image_remove'] = forum_link($forum_url['funny_avatar_remove_file'], array($id, generate_form_token('remove_file'.$forum_user['id'])));
		$forum_page['image_size'] = pun_funny_avatars_get_file_info($id);
		if ($forum_page['image_size']['height'] > 600)
		{
			$forum_page['image_size']['width'] = $forum_page['image_size']['width'] / $forum_page['image_size']['height'] * $forum_page['image_size']['width'];
			$forum_page['image_size']['height'] = 600;
		}
	}
	if (in_array('result_link', $view_blocks))
	{
		$forum_page['rewrite_avatar'] = forum_link($forum_url['funny_avatar_rewrite_avatar'], array($id, generate_form_token('rewrite_avatar'.$forum_user['id'])));
		$forum_page['rewrite_avatar_notice'] = pun_funny_avatars_get_avatar_type($id) ? $lang_pun_funny_avatars['Replace avatar'] : $lang_pun_funny_avatars['Create avatar'];
	}
	if (in_array('upload_form', $view_blocks))
	{
		$forum_page['upload_help'][] = $lang_pun_funny_avatars['Funny avatars upload info none'];
		$forum_page['upload_help'][] = $lang_pun_funny_avatars['Funny avatars upload info type'];
		$forum_page['upload_help'][] = sprintf($lang_pun_funny_avatars['Funny avatars upload info size'], $forum_config['o_pun_funny_avatars_max_width'], $forum_config['o_pun_funny_avatars_max_height'], forum_number_format($forum_config['o_pun_funny_avatars_max_size']), forum_number_format(ceil($forum_config['o_pun_funny_avatars_max_size'] / 1024)));
	}
	if (in_array('effects', $view_blocks))
	{
		if (!$funny_templates['FET']['error'])
		{
			$tmp_preview_images = array();
			if (!$funny_templates['FET']['error'])
			{
				foreach ($funny_templates['FET']['templates'] as $group => $template_list)
					foreach ($template_list as $template_index => $template_info)
						$tmp_preview_images[] = '"'.$template_info['thumb'].'"';
				$forum_page['preview_fet_templates_str'] = implode(',', $tmp_preview_images);
			}
			unset($tmp_preview_images);
		}	
	}
	// Setup breadcrumbs
	$forum_page['crumbs'] = array(
		array($forum_config['o_board_title'], forum_link($forum_url['index'])),
		array(sprintf($lang_profile['Users profile'], $user['username']), forum_link($forum_url['user'], $id)),
		array($lang_profile['Section avatar'],forum_link($forum_url['funny_avatar'], $id))
	);

	// Setup the form
	$forum_page['group_count'] = $forum_page['item_count'] = $forum_page['fld_count'] = 0;

	define('FORUM_PAGE', 'profile-funny-avatar');
	require FORUM_ROOT.'header.php';

	// START SUBST - <!-- forum_main -->
	ob_start();

?>
	<div class="main-content main-frm">
<?php

	$blocks_order = array('upload_form', 'image_link', 'result_link', 'effects');
	foreach ($blocks_order as $block)
	{
		if (in_array($block, $view_blocks))
			include $ext_info['path'].'/view/block_'.$block.'.php';
	}

?>
	</div>

<?php

	$tpl_temp = forum_trim(ob_get_contents());
	$tpl_main = str_replace('<!-- forum_main -->', $tpl_temp, $tpl_main);
	ob_end_clean();
	// END SUBST - <!-- forum_main -->

	require FORUM_ROOT.'footer.php';
}

if (!defined('PUN_EXTENSIONS_USED') && !empty($pun_extensions_used))
{
	define('PUN_EXTENSIONS_USED', 1);
	if (count($pun_extensions_used) == 1)
		echo '<p style="clear: both; ">The '.$pun_extensions_used[0].' official extension is installed. Copyright &copy; 2003&ndash;2009 <a href="http://punbb.informer.com/">PunBB</a>.</p>';
	else
		echo '<p style="clear: both; ">Currently installed <span id="extensions-used" title="'.implode(', ', $pun_extensions_used).'.">'.count($pun_extensions_used).' official extensions</span>. Copyright &copy; 2003&ndash;2009 <a href="http://punbb.informer.com/">PunBB</a>.</p>';
}
